package pl.skawianna.uiimport java.util.Listimport org.apache.commons.logging.LogFactory;import org.apache.log4j.Logger;import org.springframework.dao.DataIntegrityViolationException;import org.springframework.transaction.HeuristicCompletionException;import org.springframework.transaction.TransactionException;import org.springframework.transaction.TransactionSystemException;import pl.skawianna.Userimport pl.skawianna.UserServiceimport com.vaadin.grails.Grailsimport com.vaadin.ui.Buttonimport com.vaadin.ui.CustomComponentimport com.vaadin.ui.Labelimport com.vaadin.ui.TextFieldimport com.vaadin.ui.VerticalLayoutimport com.vaadin.ui.Button.ClickEventimport com.vaadin.ui.Button.ClickListener
class Welcome extends CustomComponent  {
	VerticalLayout loginLayout	TextField txtLogin	Label lblError	Button btnOk	UserService userService = Grails.get(UserService)	private final static Logger log = Logger.getLogger(Welcome.class.getName());		public Welcome() {		buildMainLayout()		setCompositionRoot(loginLayout)	}		private void buildMainLayout() {		loginLayout = new VerticalLayout()		txtLogin = new TextField()		lblError = new Label()		btnOk = new Button()		txtLogin.setCaption(Grails.i18n("welcome.login"))		txtLogin.setRequired(true)		btnOk.setCaption(Grails.i18n("welcome.enter"))		btnOk.addClickListener(new ClickListener() {			@Override			public void buttonClick(ClickEvent event) {								try {					log.info "registering user: ${txtLogin.value}" 					userService.registerUser(txtLogin.value)					log.info "registered user. listing users:"					List<User> users = userService.getUsers()					log.info "listed users: $users"										for (User user : users) {						lblError.value = ""						loginLayout.addComponent(new Label(user.getUsername()))					}				}				catch (TransactionException e){					if ((e?.cause instanceof TransactionSystemException) 						&& (e?.cause?.cause instanceof DataIntegrityViolationException)){						lblError.value = Grails.i18n('welcome.loginMustBeUnique')					} else {						log.error(e.stackTrace)					}				}			}		})		loginLayout.setSizeFull()		loginLayout.addComponent(txtLogin)		loginLayout.addComponent(lblError)		loginLayout.addComponent(btnOk)	}	}